import React, { Component } from 'react';
import axios from 'axios';

import { Link } from 'react-router-dom';

export default class PropAtelier extends Component {

    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }
    componentDidMount() {
        axios.get(`https://mampmeback.herokuapp.com/api/users/newArticle/${localStorage.id}`)
            .then(response => {
                console.log('user-article ==== ', response)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <div className="name" id="tableau">
            {/* <thead>
                <tr>
                    <th>titre</th>
                    <th>prix</th>
                    <th>description</th>
                    <th>photo</th>
                    <th>Durée</th>
                    <th>Reservation</th>
                    <th>Date</th>
                    <th>Heure debut</th>
                    <th>P.disponible</th>
                    <th>actions</th>
                </tr>
            </thead> */}
            <div>
                {
                    (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {

                        return <tr key={obj._id}>
                             <div>
                                <img width="150px" height="50px" src={'https://mampmeback.herokuapp.com/api/users/newArticleImage/' + obj.image} alt="image" />
                            </div>
                        <div>{obj.titre}</div>
                        <div>{obj.prix}</div>
                        <div id="des">{obj.description}</div>

                            <div>{obj.duree} </div>
                            <div>{obj.placeRes}</div>
                            <div>{obj.date}</div>
                            <div>{obj.debut}</div>
                            <div>{obj.place}</div>
                            <div>
                                <Link to={"/modifierAtl/" + obj._id} class="btn btn-warning">Modifier</Link>


                                {obj.visib == true ? (<button class="btn btn-success" onClick={(e) => {
                                    e.preventDefault()
                                    axios.get("https://mampmeback.herokuapp.com/api/users/cacherAtl/" + obj._id).then(res => {
                                        axios.get('https://mampmeback.herokuapp.com/api/users/newArticle/' + localStorage.id).then(res => {
                                            console.log(res.data)
                                            this.setState({ profil: res.data })
                                        })
                                        console.log(res.data)
                                    })


                                }}></button>) : (<button class="btn btn-warning" onClick={(e) => {
                                    e.preventDefault()
                                    console.log(obj._id)
                                    axios.get("https://mampmeback.herokuapp.com/api/users/affichAtl/" + obj._id).then(res => {
                                        axios.get('https://mampmeback.herokuapp.com/api/users/newArticle/' + localStorage.getItem('id')).then(res => {
                                            console.log(res.data)
                                            this.setState({ profil: res.data })
                                        })
                                        console.log(res.data)
                                    })

                                }}></button>)}</div>
                        </tr>

                    })) : ('')
                }
            </div>
        </div>
    }
    render() {
        return (
            <div className='app1'>
                {this.liste()}
            </div>
        );
    }
}
