import React, { Component } from 'react';
import axios from 'axios';

import { Link } from 'react-router-dom';

export default class PropAtelier extends Component {

    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }
    componentDidMount() {
        axios.get(`https://henrikely.herokuapp.com/api/users/newArticle/${localStorage.id}`)
            .then(response => {
                console.log('user-article ==== ', response)
                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return  <div className ="container"   id="tableau">

        <div className="table table-bordered">
            {/* <thead>
                <tr>
                    <th>titre</th>
                    <th>prix</th>
                    <th>description</th>
                    <th>photo</th>
                    <th>Durée</th>
                    <th>Reservation</th>
                    <th>Date</th>
                    <th>P.disponible</th>
                    <th>actions</th>
                </tr>
            </thead> */}

                {
                    (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {

                        return <tr key={obj._id}>

                             <p>
                                <img width="150px" height="50px" src={'https://henrikely.herokuapp.com/api/users/newArticleImage/' + obj.image} alt="pdp" />
                            </p>
                             <label>Titre</label>
                                 <p>{obj.titre}</p> <br/>
                            <label>Prix</label> <br/> <p>{obj.prix}</p>
                            <label>Description</label> <br/> <p id="des">{obj.description}</p>
                            <label>Durer</label> <br/> <p>{obj.duree} </p>
                            <label>Place reservée</label> <br/> <p>{obj.placeRes}</p>
                            <label>Date</label> <br/> <p>{obj.date}</p>
                            <label>Place disponible</label> <br/> <p>{obj.place}</p>
                            <div>
                                <Link to={"/modifierAtl/" + obj._id}  class="btn btn-warning">Modifier</Link>

                            {obj.visib == true ? (<button  class="btn btn-success" onClick={(e) => {
                                e.preventDefault()
                                axios.get("https://henrikely.herokuapp.com/api/users/cacherAtl/" + obj._id).then(res => {
                                    axios.get('https://henrikely.herokuapp.com/api/users/newArticle/' + localStorage.id).then(res => {
                                        console.log(res.data)
                                        this.setState({ profil: res.data })
                                    })
                                    console.log(res.data)
                                })


                            }}></button>) : (<button   class="btn btn-warning" onClick={(e) => {
                                e.preventDefault()
                                console.log(obj._id)
                                axios.get("https://henrikely.herokuapp.com/api/users/affichAtl/" + obj._id).then(res => {
                                    axios.get('https://henrikely.herokuapp.com/api/users/newArticle/' + localStorage.getItem('id')).then(res => {
                                        console.log(res.data)
                                        this.setState({ profil: res.data })
                                    })
                                    console.log(res.data)
                                })

                            }}></button>)}</div>
                        </tr>

                    })) : ('')
                }

        </div>

        </div>
    }
    render() {
        return (
            <div className='app1'>
                {this.liste()}
            </div>
        );
    }
}
